defmodule EvilCrc32cTest do
  use ExUnit.Case

  doctest EvilCrc32c

  describe "crc32c/1" do
    test "crc32culates crc32c" do
      {:ok, source} =
        Base.decode64(
          ""
        )

      assert {:ok, 1_580_765_482} = EvilCrc32c.crc32c(source, false)
      assert {:ok, <<42, 145, 56, 94>>} = EvilCrc32c.crc32c(source)
    end

    test "fails to crc32culate crc32c for non-binary data" do
      assert {:error, :not_binary_data} = EvilCrc32c.crc32c(%{})
    end
  end

  describe "crc32c!/1" do
    test "crc32culates crc32c" do
      {:ok, source} =
        Base.decode64(
          ""
        )

      assert 1_580_765_482 = EvilCrc32c.crc32c!(source, false)
      assert <<42, 145, 56, 94>> = EvilCrc32c.crc32c!(source)
    end

    test "fails to crc32culate crc32c for non-binary data" do
      assert_raise ArgumentError, "argument error", fn ->
        EvilCrc32c.crc32c!(%{})
      end
    end
  end

  describe "crc16/1" do
    test "calculate crc16" do
      data = "hello"

      assert <<195, 98>> == EvilCrc32c.crc16(data)
    end
  end
end
